#pragma once

/*

https://leetcode.com/problems/shortest-palindrome/description/

214. Shortest Palindrome
DescriptionHintsSubmissionsDiscussSolution
Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it. Find and return the shortest palindrome you can find by performing this transformation.

For example:

Given "aacecaaa", return "aaacecaaa".

Given "abcd", return "dcbabcd".

Credits:
Special thanks to @ifanchu for adding this problem and creating all test cases. Thanks to @Freezen for additional test cases.

*/

#include <vector>
#include <string>
#include <iostream>

using namespace std;

namespace SmartLib
{
	class ShortestPalindrome
	{
	private:
		const char _TRUE{ 'T' };
		const char _FALSE{ 'F' };
		const char _NULL{ 'T' };

	private:
		const string & _str;
		vector<vector<char>> _saved;

	public:
		ShortestPalindrome(const string & str) :
			_str(str)
		{
			_saved.resize(_str.size() + 1);
			for (auto & vec : _saved)
			{
				vec.resize(_str.size() + 1, _NULL);
			}
		}

		bool IsPalindrome(int ii, int jj)
		{
			char saved = _saved[ii][jj];
			if (saved != _NULL)
			{
				return saved == _TRUE;
			}

			bool rv = false;
			if (ii == jj)
			{
				rv = true;
			}
			else if (ii + 1 == jj)
			{
				rv = (_str[ii] == _str[jj]);
			}
			else
			{
				rv = (_str[ii] == _str[jj] && IsPalindrome(ii + 1, jj - 1));
			}

			rv ? _saved[ii][jj] = _TRUE : _saved[ii][jj] = _FALSE;

			return rv;
		}

		int MaxIndex()
		{
			for (int ii = _str.size() - 1; ii >= 0; -- ii)
			{
				if (IsPalindrome(0, ii))
				{
					return ii;
				}
			}
		}


		string shortestPalindrome()
		{
			string str;
			int maxIndex = MaxIndex();
			for (int ii = _str.size() - 1; ii > maxIndex; -- ii)
			{
				str += _str[ii];
			}
			str += _str;

			return str;
		}


		static string shortestPalindrome(const string & str)
		{
			if (0 == str.size())
			{
				return str;
			}

			ShortestPalindrome sp(str);
			return sp.shortestPalindrome();

		}
	};



	class ShortestPalindrome2
	{
	public:
		static void Reverse(string & str)
		{
			auto p1 = &str[0];
			auto p2 = &str[str.size() - 1];
			while (p1 < p2)
			{
				auto temp = *p1;
				*p1 = *p2;
				*p2 = temp;
				++p1;
				--p2;
			}
		}

		static bool IsEqual(const string & str1, const string & str2, int skippedSize)
		{
			bool rv = true;
			auto p1 = &str1[0];
			auto p2 = &str2[skippedSize];

			int size = str1.size() - skippedSize;
			//for (int ii = 0; ii < size; ++ ii)
			//{
			//	if (*p1 != *p2)
			//	{
			//		rv = false;
			//		break;
			//	}

			//	++p1;
			//	++p2;
			//}

			rv = (0 == memcmp(p1, p2, size));

			return rv;
		}

		static string ShortestPalindrome(const string & str)
		{
			auto reversed = str;
			Reverse(reversed);
			int skippedSize = 0;
			for (; skippedSize < str.size(); ++ skippedSize)
			{
				if (IsEqual(str, reversed, skippedSize))
				{
					break;
				}
			}

			//auto p = &str[str.size() - skippedSize];
			//for (int ii = 0; ii < skippedSize; ++ ii)
			//{
			//	reversed += *p;
			//	++p;
			//}

			reversed += str.substr(str.size() - skippedSize);

			return reversed;
		}
	};

	class ShortestPalindrome2Test
	{
	public:
		static void Case0()
		{
			string str{"abaaa"};

			//string str{ "" };

			string rv = ShortestPalindrome2::ShortestPalindrome(str);

			cout << str << " --> " << rv << endl;
		}
	};


}